import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Finish from "./Finish";
function Test() {
  const id = useParams();
  const [data, setData] = useState([]);
  const [qust, setQust] = useState([]);
  const [count, setCount] = useState(0);
  const [ans, setAns] = useState([]);                                                                                                                                                              

  useEffect(() => {
    fetch("http://interviewapi.ngminds.com/getQuizData")
      .then((response) => response.json())
      .then((json) => setData(json.tests));
  }, []);

  // if (data.length) {
  //   if(id.id=="AngularJS Test"){
  //     setQust(data[0].questions);
  //   }else if (id.id=="Javascript Test") {
  //     setQust(data[1].questions);
  //   }else{
  //     setQust(data[2].questions);
  //   }
  // }
  const f = data.find((el) => {
    return el.name === id.id;
  });

  const anss = qust.map((el) => {
    return el.correctOptionIndex;
  });

  useEffect(() => {
    if (f) {
      setQust(f.questions)
    }
  }, [f]);

  const incre = () => {
    if (count < qust.length - 1) {
      setCount((prev) => prev + 1);
    } else {
      setCount(0);
      end();
    }
  };
  const check = (e) => {
    setAns({ ...ans, [count]: e });
    e = "";
    console.log(ans);
  };
  const end = () => {
    const correct = anss.filter((ind, id) => {
      return ind == ans[id];
    });
    const wrong = anss.filter((ind, id) => {
      return ind != ans[id];
    });
    console.log("corr", correct.length);
    console.log("wrong", wrong.length);
    <Finish corr={correct.length} wrong={wrong.length} name={id.id}/>
  };
  if (qust.length) {
    return (
      <div className="container">
        <div className="row">
          <h1>My Interview Portal</h1>
          <hr />
          <div className="col-md-12">
            <div className="panel panel-default">
              <div className="panel-heading">{id.id}</div>
              <div className="panel-body">
                <form>
                  <label>{qust[count].questionText}</label>
                  {qust[count].options.map((op, id) => {
                    return (
                      <div className="radio" key={id}>
                        <label>
                          <input
                            type="radio"
                            name="option"
                            value={`${id}`}
                            onChange={(e) => check(e.target.value)}
                          />
                          {op}
                        </label>
                      </div>
                    );
                  })}
                </form>

                <div className="panel-footer">
                  <a className="btn btn-success" onClick={() => incre()}>
                    Next
                  </a>
                  <a href="/finish"
                    className="pull-right btn btn-danger"
                    onClick={() => end()}
                  >
                    Finish
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    return "";
  }
}

export default Test;
