import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Qustions from "./Qustions";
import Finish from "./Finish";
function Test() {
  const id = useParams();
  const [data, setData] = useState([]);
  const [qust, setQust] = useState([]);
  const [Count, setCount] = useState(3);
  useEffect(() => {
    fetch("http://interviewapi.ngminds.com/getQuizData")
      .then((response) => response.json())
      .then((json) => setData(json.tests));
  }, []);






  const handleAnswerOptionClick = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		}

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
  {questions[currentQuestion].answerOptions.map((answerOption) => (
    <button onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}>
      {answerOption.answerText}</button>))}










  useEffect(() => {
    if (data.length) {
      if (id.id === "AngularJS Test") {
        setQust(data[0].questions);
        console.log("AngularJS Test");
      } else if (id.id === "Javascript Test") {
        setQust(data[1].questions);
        console.log("Javascript Test");
      } else {
        setQust(data[2].questions);
        console.log("NodeJS Test");
      }
    }
  }, [data.length]);

  const count = (inp) => {
    if (inp === "inc") {
      setCount((prev) => prev + 1);
    } else {
      setCount((prev) => prev - 1);
    }
  };
  if (qust.length) {
    return (
      <div className="container">
        <div className="row">
          <h1>My Interview Portal</h1>
          <hr />
          <div className="col-md-12">
            <div className="panel panel-default">
              <div className="panel-heading">{id.id}</div>
              <div className="panel-body">
                <Qustions qust={qust[Count]} />
                <div className="panel-footer">
                  <button
                    className="btn btn-success "
                    onClick={() => count("inc")}
                  >
                    Prev
                  </button>
                  <button
                    className="btn btn-success"
                    onClick={() => count("dec")}
                  >
                    Next
                  </button>
                  <a
                    href="/finish"
                    className="pull-right btn btn-danger"
                    // onClick={() => end()}
                  >
                    Finish
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Test;
