import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";
import Question from "./Question";
import axios from "axios"
function Test({  setResult }) {
  const id = useParams();
  const [data, setData] = useState([]);
  const [qData, setQData] = useState([]);
  const [qNum, setQNum] = useState(0);
  const navigate = useNavigate();
  useEffect(() => {
    (async () => {
      try {
        const result = await axios.get(
"https://dip-kaluse.github.io/examport/portal.json")
        setData(result.data.tests)
      } catch (error) {
        console.error(error);
      }
    })()
  },[id])
  useEffect(() => {
    console.log(data);
    console.log(id.id);
    data.length
      ? setQData(data.find((el) => el.name === id.id))
      : setQData(qData);
  }, [data]);
  const shuf = (type) => {
    // localStorage.setItem(JSON.stringify(qData.name), JSON.stringify(test));
  };
  const onSubmitHandler = () => {
    // let result = Object.keys(ans).filter(
    //   (key) => JSON.stringify(test[key]) === JSON.stringify(qAns[key])
    // );
    // console.log(qAns);
    // setResult(result.length);
    navigate("/finish");
  };
  if (qData.length !== 0) {
    return (
      <div className="container">
        <div className="row">
          <h1>My Interview Portal</h1>
          <hr />
          <div className="col-md-12">
            <div className="panel panel-default">
              <div className="panel-heading">{qData.name}</div>
              <div className="panel-body">
                <Question
                  quest={qData.questions[qNum]}
                  data={qData}
                  count={qNum}
                  name={qData.name}
                />
                <div className="panel-footer">
                  <button
                    className="btn btn-success"
                    onClick={() => {
                      setQNum((prev) => prev - 1);
                      shuf("Dec");
                    }}
                    disabled={qNum === 0}
                  >
                    Prev
                  </button>
                  <button
                    className="btn btn-success"
                    onClick={() => {
                      setQNum((prev) => prev + 1);
                      shuf("inc");
                    }}
                    disabled={qNum > qData.questions.length - 2}
                  >
                    Next
                  </button>
                  <button
                    className="pull-right btn btn-danger"
                    onClick={() => onSubmitHandler()}
                  >
                    Finish
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    return "";
  }
}
export default Test;
