import React, { useState } from 'react'

function Qustions({qust}) {
    const [ans,setAns]=useState(0)
    const [checked, setChecked] = useState([]);
    const changeRadio = (e,id) => {

        setChecked([...checked,{[id]:e.target.value}]);
      };
      console.log(checked);
    if (qust.type==="Multiple-Response") {
        return (
            <form >
            <label>{qust.questionText}</label>
            {

              qust.options.map((el,id)=>{
                return (
                    <div className="radio" key={id}>
                    <label>
                      <input
                        type="checkbox"
                        name="option"  
                        checked={checked.id} 
                        value={id}
                        onChange={changeRadio}
                      />
                      {el}
                    </label>
                  </div>
                )
              })
            }
            </form>
              )
    }else{
        return (
            <form >
            <label>{qust.questionText}</label>
            {

              qust.options.map((el,id)=>{
                return (
                    <div className="radio" key={id}>
                    <label>
                      <input
                        type="radio"
                        name="option"
                        checked={checked.id}
                        value={`${id}`}
                        onChange={(e)=>changeRadio(e,qust.questionText)}
                      />
                      {el}
                    </label>
                  </div>
                )
              })
            }
            </form>
              )
    }

}

export default Qustions
