import React, { useEffect, useState } from "react";
function Question({ quest, count, name, data }) {
  const [test, setTest] = useState([
    [false, false, false, false],
    [false, false, false, false],
  ]);


  console.log(test);
  console.log(quest.type);
  const onClickHandler = (e) => {
    console.log(test[count]);
    let a=[...test]
    console.log(a);
    if ((quest.type = undefined)) {
      a[count][e.target.value] = !a[count][e.target.value];
      setTest(a)
    } else {
      a[count] = [false, false, false, false];
      a[count][e.target.value] = !a[e.target.value];
      setTest(a)
    }

  };
  return (
    <form>
      <label>{quest.questionText}</label>
      {quest.options.map((op, id) => {
        return (
          <div className="radio" key={id}>
            <label>
              <input
                type={quest.type !== "Multiple-Response" ? "radio" : "checkbox"}
                name="option"
                checked={test[count][id]}
                value={id}
                onChange={(e) => onClickHandler(e)}
              />
              {op}
            </label>
          </div>
        );
      })}
    </form>
  );
}
export default Question;
