import React, { useEffect, useState } from "react";
function Question({ quest, count, name,data }) {
  const [test, setTest] = useState([false,false,false,false]);
// useEffect(()=>{
// setTest(
//   JSON.parse(localStorage.getItem(JSON.stringify(name))) !== null
//     ? JSON.parse(localStorage.getItem(JSON.stringify(name)))
//     : data.questions.map((el, id) =>
//     new Array(data.questions[id].options.length).fill(false)
//   )
// );
// },[ ])




useEffect(() => {
  fetch("http://interviewapi.ngminds.com/getQuizData")
    .then((response) => response.json())
    .then((json) =>
      json.tests.length
        ? setData(json.tests.find((el) => el.name === id.id))
        : setData(data)
    );
}, [id]);
console.log(test);
  const onClickHandler = (e) => {
    if (quest.type !== undefined) {
      e.target.checked
        ? (test[e.target.value] = true)
        : (test[e.target.value] = false);
    } else {
      if (e.target.checked) {
        test = new Array(quest.options.length).fill(false);
        test[e.target.value] = true
      }
    }
    setTest(test)
    // localStorage.setItem(JSON.stringify(name),JSON.stringify(test))
  };
    return (
      <form  >
        <label>{quest.questionText}</label>
        {quest.options.map((op, id) => {
          console.log(id)
          return (
            <div className="radio" key={id}>
              <label>
                <input
                  type={
                    quest.type !== "Multiple-Response" ? "radio" : "checkbox"
                  }
                  name="option"
                  checked={test[id]}
                  value={id}
                  onChange={(e) => onClickHandler(e)}
                />
                {op}
              </label>
            </div>
          );
        })}
      </form>
    );
}
export default React.memo(Question);
